package com.jfast.web.admin.config;import org.springframework.beans.BeansException;import org.springframework.beans.factory.DisposableBean;import org.springframework.context.ApplicationContext;import org.springframework.context.ApplicationContextAware;import org.springframework.context.annotation.Lazy;import org.springframework.stereotype.Component;/** * @author zengjintao * @create 2019/6/17 10:02 * @since 1.0 **/@Component@Lazy(false)public class SpringBeanManager implements ApplicationContextAware, DisposableBean {    private static ApplicationContext applicationContext = null;    public static <T> T getBean(String name) {        return (T) applicationContext.getBean(name);    }    public static <T> T getBean(Class<T> clazz) {        return applicationContext.getBean(clazz);    }    public void destroy() throws Exception {        if (applicationContext != null) {            applicationContext = null;        }    }    public void setApplicationContext(ApplicationContext applicationContext) throws BeansException {        applicationContext = applicationContext;    }}