package com.jfast.common.constants;/** * @author zengjintao * @create 2019/3/27 16:09 * @since 1.0 **/public final class EnumConstants {    public enum SchoolType {        PRIMARY_SCHOOL(1, "小学"), MIDDLE_SCHOOL(2, "初中"), HIGH_SCHOOL(3, "高中");        private int value;        private String name;        private SchoolType(int value, String name) {            this.name = name;            this.value = value;        }        public int getValue() {            return value;        }        public String getName() {            return name;        }    }    public enum PlatformType {        WEB_ADMIN(1, "后台管理系统"), WEB_FRONT(2, "前端");        private int value;        private String name;        private PlatformType(int value, String name) {            this.name = name;            this.value = value;        }        public int getValue() {            return value;        }        public String getName() {            return name;        }    }    public enum CreateType {        SYSTEM_CREATE(1, "系统创建"), ADMIN_CREATE(2, "管理员创建");        private int value;        private String name;        private CreateType(int value, String name) {            this.name = name;            this.value = value;        }        public int getValue() {            return value;        }        public String getName() {            return name;        }    }}