mapper.insert:
insert into mbbs_subject
SUBJECT_BASIC_ID, SUBJECT_CONTENT, SUBJECT_DISPLAY, SUBJECT_FORUM_FIRST_ID, SUBJECT_FORUM_SECOND_ID
#{basicId}, #{subjectContent}, #{subjectDisplay}, #{subjectForumFirstId}, #{subjectForumSecondId}

mapper.select:
select

from v_subject


		and BASIC_APPID=#{appId}


		and basic_modelId = #{modelId}


		and PEOPLE_ID=#{peopleId}

	group by BASIC_ID
	ORDER BY BASIC_DATETIME DESC


		limit
		${page.pageNo*page.pageSize},#{page.pageSize}

select count(*) from basic


		and BASIC_APPID=#{appId}


		and BASIC_MODELID=#{modelId}


		and BASIC_PEOPLEID=#{peopleId}

mapper.delete:
delete a,b from basic a,mbbs_subject b


		and a.BASIC_APPID = #{appId}

	and a.BASIC_ID in
	#{item}
	and b.SUBJECT_BASIC_ID in
	#{item}

		and a.BASIC_PEOPLEID = #{peopleId}

mapper.select:
select count(*)
from basic


		and basic_appid=#{appId}


		and basic_categoryId=#{categoryId}


		and BASIC_DATETIME >= #{today}


		and BASIC_DATETIME < #{tomorrow}

select count(*)
from v_subject

	basic_appId = #{appId}

		and basic_modelId = #{modelId}


		and SUBJECT_DISPLAY=#{isDisplay}

select

from v_subject

	basic_appId = #{appId}

		and basic_modelId = #{modelId}


		and SUBJECT_DISPLAY=#{isDisplay}


ORDER BY basic_id DESC

	limit
	${page.pageNo*page.pageSize},#{page.pageSize}

select count(*) from v_subject


		and basic_appid=#{appId}


		and basic_modelId = #{modelId}


		and SUBJECT_DISPLAY=#{isDisplay}





			and
			FIND_IN_SET(BASIC_CATEGORYID,func_child_category(#{whereMap.categoryId}))



			and basic_title like
			CONCAT('%',#{whereMap.basicTitle},'%')

select

from v_subject


		and basic_appid=#{appId}


		and basic_modelId = #{modelId}


		and SUBJECT_DISPLAY=#{isDisplay}





			and BASIC_CATEGORYID=#{categoryId}



			and basic_title like
			CONCAT('%',#{whereMap.basicTitle},'%')



ORDER BY

	${orderBy}
	asc
	DESC


	basic_id
	asc
	DESC


	limit
	${page.pageNo*page.pageSize},#{page.pageSize}

mapper.update:
update MBBS_SUBJECT
SUBJECT_BASIC_ID = #{basicId}, SUBJECT_CONTENT=#{subjectContent}, SUBJECT_FORUM_FIRST_ID=#{subjectForumFirstId}, SUBJECT_FORUM_SECOND_ID=#{subjectForumSecondId},
where SUBJECT_BASIC_ID = #{basicId}

update basic

	BASIC_SORT=#{basicSort},


	basic_appid=#{appId} and basic_modelId = #{modelId}

		and BASIC_ID = #{basicId}


		and BASIC_CATEGORYID =#{categoryId}

mapper.select:
select count(*)
from v_subject

	basic_appId = #{appId}

		and find_in_set((select group_concat(category_id) from category
		where CATEGORY_APPID=#{appId} and
		category_title in

			#{title}

		),basic_type) > 0



		and find_in_set((select group_concat(category_id) from category
		where CATEGORY_APPID=#{appId} and
		category_title in

			#{title}

		),basic_type) = 0



		and basic_modelId = #{modelId}



		and BASIC_CATEGORYID=#{categoryId}



		and basic_title like CONCAT('%',#{ keyWord},'%')


		and SUBJECT_DISPLAY=#{isDisplay}

select

from v_subject

	basic_appId = #{appId}


		and find_in_set((select group_concat(category_id) from category
		where CATEGORY_APPID=#{appId} and
		category_title in

			#{title}

		),basic_type) > 0



		and find_in_set((select group_concat(category_id) from category
		where CATEGORY_APPID=#{appId} and
		category_title in

			#{title}

		),basic_type) = 0



		and basic_modelId = #{modelId}



		and BASIC_CATEGORYID=#{categoryId}


		and basic_title like CONCAT('%',#{ keyWord},'%')


		and SUBJECT_DISPLAY=#{isDisplay}


ORDER BY

	${orderBy}
	asc
	DESC


	basic_id
	asc
	DESC


	limit
	${page.pageNo*page.pageSize},#{page.pageSize}

select COALESCE(max(basic_sort),0)
from
`mbbs_subject` LEFT JOIN `basic`
ON `mbbs_subject`.`SUBJECT_BASIC_ID` = `basic`.`BASIC_ID`


		and basic_appid=#{appId}


		and BASIC_CATEGORYID =#{categoryId}


		and basic_modelId = #{modelId}

