from v_subject

	basic_appId = #{appId}

		and find_in_set((select group_concat(category_id) from category
		where CATEGORY_APPID=#{appId} and
		category_title in

			#{title}

		),b.basic_type) > 0


		and find_in_set((select group_concat(category_id) from category
		where CATEGORY_APPID=#{appId} and
		category_title in

			#{title}

		),b.basic_type) = 0


		and basic_modelId = #{modelId}



		and SUBJECT_FORUM_FIRST_ID=#{firstForumId}


